contracts/core/utils/ProtocolLock.sol.content:
// Copyright (c) 2020-2022. All Rights Reserved
// SPDX-License-Identifier: UNLICENSED

pragma solidity =0.7.6;
pragma abicoder v2;

import '../interfaces/IGovernor.sol';


/**
 * @title ProtocolLock
 *
 * @notice ProtocolLock provides reentrancy guard functionality at the protocol level.
 * It does so by providing enter() and exit() functions that must be called in the correct order,
 * or else it reverts the transaction. It also handles permissions around who can make these calls.
 * See Governed.sol for the code that calls enter() and exit().
 *
 * See library/ReentrancyGuard.sol by Open Zeppelin for more information on reentrancy guards.
 */
contract ProtocolLock is IProtocolLock {

    /*****************************************************
     * ==================== SETUP ====================== *
     *****************************************************/
    /// @notice The governor allowed to administer this protocol lock
    IGovernor public immutable governor;
    /// @dev Callers authorized to set and unset the lock
    mapping(address => bool) public authorizedCaller;

    constructor (ConstructorParams memory params) {
        governor = params.Governor;
    }

    /// @dev Only governor is allowed to authorize/unauthorize callers.
    modifier onlyGovernor() {
        _requireAuthorized(msg.sender == address(governor));
        _;
    }

    /*
     * @dev Add the given address as an authorized user of ProtocolLock.
     *
     * @param caller The potential user of this reentrancy guard.
     */
    function authorizeCaller(address caller) external override onlyGovernor {
        authorizedCaller[caller] = true;
        emit CallerAuthorized(address(caller));
    }

    /*
     * @dev Remove the given address as an authorized user of ProtocolLock.
     *
     * @param caller The potential user of this reentrancy guard.
     */
    function unauthorizeCaller(address caller) external override onlyGovernor {
        authorizedCaller[caller] = false;
        emit CallerUnauthorized(caller);
    }

    /*****************************************************
     * =============== GUARD FUNCTIONS ================= *
     *****************************************************/
    uint private constant _NOT_ENTERED = 1;
    uint private constant _ENTERED = 2;

    uint private _status = _NOT_ENTERED;

    /// @dev Whether or not this caller is allowed to update the reentrancy values of this lock
    modifier callerAuthorized() {
        _requireAuthorized(authorizedCaller[msg.sender]);
        _;
    }

    function _requireAuthorized(bool authorized) internal pure {
        require(authorized, 'PL: Not Authorized');
    }

    /// @dev Called before the protocol level external call starts executing. Errors if there is reentrancy.
    function enter() external override callerAuthorized {
        require(_status != _ENTERED, 'PL Reentrancy');
        _status = _ENTERED;
    }

    /// @dev Called after the protocol level external call finishes executing. Errors if there is reentrancy.
    function exit() external override callerAuthorized {
        require(_status == _ENTERED, 'PL error');
        _status = _NOT_ENTERED;
    }
}

